import React, { useState } from 'react';

const AddGame = () => {
    const [game, setGame] = useState({
        date: '',
        gameNumber: '',
        teamA: '',
        teamB: '',
        scoreA: '',
        scoreB: '',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setGame({
            ...game,
            [name]: value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('http://localhost:8080/games/save', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    date: game.date,
                    gameNumber: game.gameNumber,
                    teamA: game.teamA,
                    teamB: game.teamB,
                    scoreA: parseInt(game.scoreA, 10),
                    scoreB: parseInt(game.scoreB, 10),
                }),
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const data = await response.json();
            alert('Game added successfully: ' + data);
        } catch (error) {
            console.error('Error:', error);
            alert('Failed to add game');
        }

        setGame({
            date: '',
            gameNumber: '',
            teamA: '',
            teamB: '',
            scoreA: '',
            scoreB: ''
        });
    };

    return (
        <div className="container mt-4">
            <h2>Add Game</h2>
            <form onSubmit={handleSubmit} className="mt-3">
                <div className="mb-3">
                    <input
                        type="date"
                        name="date"
                        value={game.date}
                        onChange={handleChange}
                        className="form-control"
                        required
                    />
                </div>
                <div className="mb-3">
                    <input
                        type="text"
                        name="gameNumber"
                        value={game.gameNumber}
                        onChange={handleChange}
                        className="form-control"
                        placeholder="Game Number"
                        required
                    />
                </div>
                <div className="mb-3">
                    <input
                        type="text"
                        name="teamA"
                        value={game.teamA}
                        onChange={handleChange}
                        className="form-control"
                        placeholder="Team A"
                        required
                    />
                </div>
                <div className="mb-3">
                    <input
                        type="text"
                        name="teamB"
                        value={game.teamB}
                        onChange={handleChange}
                        className="form-control"
                        placeholder="Team B"
                        required
                    />
                </div>
                <div className="mb-3">
                    <input
                        type="number"
                        name="scoreA"
                        value={game.scoreA}
                        onChange={handleChange}
                        className="form-control"
                        placeholder="Score A"
                        required
                    />
                </div>
                <div className="mb-3">
                    <input
                        type="number"
                        name="scoreB"
                        value={game.scoreB}
                        onChange={handleChange}
                        className="form-control"
                        placeholder="Score B"
                        required
                    />
                </div>
                <button type="submit" className="btn btn-primary">Add Game</button>
            </form>
        </div>
    );
};

export default AddGame;
