import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';

const GameStatsRecorder = () => {
    const location = useLocation();
    const { selectedPlayers, selectedGame } = location.state || { selectedPlayers: [], selectedGame: '' };

    const [stats, setStats] = useState(selectedPlayers.map(player => ({
        playerId: { id: player.id },
        gameId: { id: selectedGame },
        belongedTeam: player.team,
        points: 0,
        fieldGoalMade: 0,
        fieldGoalAttempted: 0,
        threePointerMade: 0,
        threePointerAttempted: 0,
        freeThrowMade: 0,
        freeThrowAttempted: 0,
        rebounds: 0,
        assists: 0,
        steal: 0,
        block: 0,
        turnover: 0,
    })));

    const handleChange = (index, field, value) => {
        setStats(prevStats => {
            const newStats = [...prevStats];
            newStats[index][field] = value;
            return newStats;
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log('Submitting stats:', stats); // For debugging

        try {
            const response = await fetch('http://localhost:8080/game/stats', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(stats),
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const data = await response.json();
            alert('Stats submitted successfully!');
            console.log(data);
        } catch (error) {
            console.error('Error submitting stats:', error);
            alert('Failed to submit stats');
        }
    };


    return (
        <div className="container mt-5">
            <h2>Game Stats</h2>
            <h4>Selected Game ID: {selectedGame}</h4>
            {selectedPlayers.map(player => (
                <div key={player.id} className="card mb-3">
                    <div className="card-body">
                        <h5 className="card-title">{player.id}</h5>
                        <p className="card-text">Team: {player.team}</p>
                        <div className="mb-2">
                            <label className="form-label">Points</label>
                            <input type="number" className="form-control" placeholder="Points" />
                        </div>
                        <div className="mb-2">
                            <label className="form-label">Assists</label>
                            <input type="number" className="form-control" placeholder="Assists" />
                        </div>
                        <div className="mb-2">
                            <label className="form-label">Rebounds</label>
                            <input type="number" className="form-control" placeholder="Rebounds" />
                        </div>
                    </div>
                </div>
            ))}
        </div>
    );
};

export default GameStatsRecorder;
