import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';

const GameStatsRecorder = () => {
    const location = useLocation();
    const { selectedPlayers, selectedGame, games } = location.state || { selectedPlayers: [], selectedGame: '', games: null };

    const selectedGameData = games;

    const [stats, setStats] = useState(selectedPlayers.map(player => ({
        playerId: { id: player.id },
        gameId: { id: selectedGame },
        belongedTeam: player.team,
        points: 0,
        fieldGoalMade: 0,
        fieldGoalAttempted: 0,
        threePointerMade: 0,
        threePointerAttempted: 0,
        freeThrowMade: 0,
        freeThrowAttempted: 0,
        rebounds: 0,
        assists: 0,
        steal: 0,
        block: 0,
        turnover: 0,
    })));

    const handleIncrement = (index, field) => {
        setStats(prevStats => {
            const newStats = [...prevStats];
            newStats[index][field] += 1; // Increment by 1
            return newStats;
        });
    };

    const handleDecrement = (index, field) => {
        setStats(prevStats => {
            const newStats = [...prevStats];
            newStats[index][field] = Math.max(0, newStats[index][field] - 1); // Decrement by 1 but not below 0
            return newStats;
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log('Submitting stats:', stats); // For debugging

        try {
            const response = await fetch('http://localhost:8080/games/stats/save', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(stats),
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const data = await response.json();
            alert('Stats submitted successfully!');
            console.log(data);
        } catch (error) {
            console.error('Error submitting stats:', error);
            alert('Error submitting, but submitted');
        }
    };


    return (
        <div className="container mt-5">
            <h4>Selected Game ID: { selectedGame} {selectedGameData?.gameNumber} ({selectedGameData?.date})</h4>
            {selectedPlayers.map((player, index) => (
                <div key={player.id} className="card mb-3">
                    <div className="card-body">
                        <h5 className="card-title">Player ID: {player.id} ({player.team})</h5>
                        <form onSubmit={handleSubmit}>
                            <div className="mb-2">
                                <label className="form-label">Points</label>
                                <input
                                    type="number"
                                    className="form-control"
                                    placeholder="Points"
                                    value={stats[index].points}
                                    onChange={(e) => handleChange(index, 'points', e.target.value)}
                                />
                            </div>
                            <div className="mb-2">
                                <label className="form-label">Field Goals Made</label>
                                <input
                                    type="number"
                                    className="form-control"
                                    placeholder="Field Goals Made"
                                    value={stats[index].fieldGoalMade}
                                    onChange={(e) => handleChange(index, 'fieldGoalMade', e.target.value)}
                                />
                            </div>
                            <div className="mb-2">
                                <label className="form-label">Field Goals Attempted</label>
                                <input
                                    type="number"
                                    className="form-control"
                                    placeholder="Field Goals Attempted"
                                    value={stats[index].fieldGoalAttempted}
                                    onChange={(e) => handleChange(index, 'fieldGoalAttempted', e.target.value)}
                                />
                            </div>
                            <div className="mb-2">
                                <label className="form-label">Three-Pointers Made</label>
                                <input
                                    type="number"
                                    className="form-control"
                                    placeholder="Three-Pointers Made"
                                    value={stats[index].threePointerMade}
                                    onChange={(e) => handleChange(index, 'threePointerMade', e.target.value)}
                                />
                            </div>
                            <div className="mb-2">
                                <label className="form-label">Three-Pointers Attempted</label>
                                <input
                                    type="number"
                                    className="form-control"
                                    placeholder="Three-Pointers Attempted"
                                    value={stats[index].threePointerAttempted}
                                    onChange={(e) => handleChange(index, 'threePointerAttempted', e.target.value)}
                                />
                            </div>
                            <div className="mb-2">
                                <label className="form-label">Free Throws Made</label>
                                <input
                                    type="number"
                                    className="form-control"
                                    placeholder="Free Throws Made"
                                    value={stats[index].freeThrowMade}
                                    onChange={(e) => handleChange(index, 'freeThrowMade', e.target.value)}
                                />
                            </div>
                            <div className="mb-2">
                                <label className="form-label">Free Throws Attempted</label>
                                <input
                                    type="number"
                                    className="form-control"
                                    placeholder="Free Throws Attempted"
                                    value={stats[index].freeThrowAttempted}
                                    onChange={(e) => handleChange(index, 'freeThrowAttempted', e.target.value)}
                                />
                            </div>
                            <div className="mb-2">
                                <label className="form-label">Rebounds</label>
                                <input
                                    type="number"
                                    className="form-control"
                                    placeholder="Rebounds"
                                    value={stats[index].rebounds}
                                    onChange={(e) => handleChange(index, 'rebounds', e.target.value)}
                                />
                            </div>
                            <div className="mb-2">
                                <label className="form-label">Assists</label>
                                <input
                                    type="number"
                                    className="form-control"
                                    placeholder="Assists"
                                    value={stats[index].assists}
                                    onChange={(e) => handleChange(index, 'assists', e.target.value)}
                                />
                            </div>
                            <div className="mb-2">
                                <label className="form-label">Steals</label>
                                <input
                                    type="number"
                                    className="form-control"
                                    placeholder="Steals"
                                    value={stats[index].steal}
                                    onChange={(e) => handleChange(index, 'steal', e.target.value)}
                                />
                            </div>
                            <div className="mb-2">
                                <label className="form-label">Blocks</label>
                                <input
                                    type="number"
                                    className="form-control"
                                    placeholder="Blocks"
                                    value={stats[index].block}
                                    onChange={(e) => handleChange(index, 'block', e.target.value)}
                                />
                            </div>
                            <div className="mb-2">
                                <label className="form-label">Turnovers</label>
                                <input
                                    type="number"
                                    className="form-control"
                                    placeholder="Turnovers"
                                    value={stats[index].turnover}
                                    onChange={(e) => handleChange(index, 'turnover', e.target.value)}
                                />
                            </div>
                        </form>
                    </div>
                </div>
            ))}
            <button type="submit" className="btn btn-primary" onClick={handleSubmit}>Submit Stats</button>
        </div>
    );
};

export default GameStatsRecorder;
