import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const AddGameStats = () => {
    const [numPlayers, setNumPlayers] = useState(2);
    const [isOpen, setIsOpen] = useState(false);
    const [players, setPlayers] = useState([]);
    const [selectedPlayers, setSelectedPlayers] = useState([]);
    const [showModal, setShowModal] = useState(false);
    const navigate = useNavigate();

    // Fetch players from the server
    useEffect(() => {
        const fetchPlayers = async () => {
            try {
                const response = await fetch('http://localhost:8080/players');
                const data = await response.json();
                setPlayers(data);
            } catch (error) {
                console.error('Error fetching players:', error);
            }
        };

        fetchPlayers();
    }, []);

    const handlePlayerSelect = (playerId, team) => {
        setSelectedPlayers((prev) => {
            const existingPlayer = prev.find(p => p.id === playerId);
            if (existingPlayer) {
                return prev.map(p =>
                    p.id === playerId ? { ...p, team } : p
                );
            }
            return [...prev, { id: playerId, team }];
        });
    };

    const handleProceed = () => {
        console.log('Selected Players:', selectedPlayers);
        navigate('/game/game_stats', { state: { selectedPlayers } });
        setShowModal(false);
    };

    return (
        <div className="container mt-5">
            <h2>Add Game Stats</h2>
            <div className="mb-3">
                <label className="form-label">Number of Players</label>
                <select
                    value={numPlayers}
                    onChange={(e) => setNumPlayers(e.target.value)}
                    className="form-select"
                >
                    {[...Array(9).keys()].map(i => (
                        <option key={i + 2} value={i + 2}>{i + 2}</option>
                    ))}
                </select>
            </div>
            <button
                type="button"
                className="btn btn-primary"
                onClick={() => setShowModal(true)}
            >
                Select Players
            </button>

            {/* Modal for selecting players */}
            <div
                className={`modal fade ${showModal ? 'show' : ''}`}
                id="playerModal"
                tabIndex="-1"
                aria-labelledby="playerModalLabel"
                aria-hidden={!showModal}
                style={{ display: showModal ? 'block' : 'none' }}
            >
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="playerModalLabel">Select Players</h5>
                            <button
                                type="button"
                                className="btn-close"
                                onClick={() => setShowModal(false)}
                                aria-label="Close"
                            ></button>
                        </div>
                        <div className="modal-body">
                            {players.map(player => (
                                <div key={player.id} className="mb-2 d-flex align-items-center">
                                    <input
                                        type="checkbox"
                                        onChange={(e) => handlePlayerSelect(player.id, e.target.checked ? 'teamA' : '')}
                                        className="me-2"
                                    />
                                    <span>{player.name}</span>
                                    <select
                                        onChange={(e) => handlePlayerSelect(player.id, e.target.value)}
                                        defaultValue="teamA"
                                        className="form-select ms-2"
                                    >
                                        <option value="teamA">Team A</option>
                                        <option value="teamB">Team B</option>
                                    </select>
                                </div>
                            ))}
                        </div>
                        <div className="modal-footer">
                            <button
                                type="button"
                                className="btn btn-secondary"
                                onClick={() => setShowModal(false)}
                            >
                                Close
                            </button>
                            <button
                                type="button"
                                className="btn btn-primary"
                                onClick={handleProceed}
                            >
                                Proceed to Stats
                            </button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default AddGameStats;
