import React, { useState } from 'react';

const AddGame = ({ }) => {
    const [game, setGame] = useState({
        date: '',
        gameNumber: '',
        teamA: '',
        teamB: '',
        scoreA: '',
        scoreB: '',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setGame({
            ...game,
            [name]: value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch('http://localhost:8080/games/save', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    date: game.date,
                    gameNumber: game.gameNumber,
                    teamA: game.teamA,
                    teamB: game.teamB,
                    scoreA: parseInt(game.scoreA, 10),
                    scoreB: parseInt(game.scoreB, 10),
                }),
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const data = await response.json();
            alert('Game added successfully: ' + data);
        } catch (error) {
            console.error('Error:', error);
            // alert('Failed to add game');
        }

        onAddGame(game);
        setGame({
            date: '',
            gameNumber: '',
            teamA: '',
            teamB: '',
            scoreA: '',
            scoreB: ''
        });
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="date"
                name="date"
                value={game.date}
                onChange={handleChange}
                required
            />
            <input
                type="text"
                name="gameNumber"
                value={game.gameNumber}
                onChange={handleChange}
                placeholder="Game Number"
                required
            />
            <input
                type="text"
                name="teamA"
                value={game.teamA}
                onChange={handleChange}
                placeholder="Team A"
                required
            />
            <input
                type="text"
                name="teamB"
                value={game.teamB}
                onChange={handleChange}
                placeholder="Team B"
                required
            />
            <input
                type="number"
                name="scoreA"
                value={game.scoreA}
                onChange={handleChange}
                placeholder="Score A"
                required
            />
            <input
                type="number"
                name="scoreB"
                value={game.scoreB}
                onChange={handleChange}
                placeholder="Score B"
                required
            />
            <button type="submit">Add Game</button>
        </form>
    );
};

export default AddGame;
