import React, { useState } from 'react';

const AddPlayer = ({ onAddPlayer}) => {
    const [player, setPlayer] = useState({
        name: '',
        height: '',
        weight: '',
        age: '',
        birthdate: '',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setPlayer({
            ...player,
            [name]: value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        onAddPlayer(player);
        setPlayer({});

        try {
            const response = await fetch('http://localhost:8080/players/save', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    name: player.name,
                    height: parseFloat(player.height),
                    weight: parseFloat(player.weight),
                    age: parseInt(player.age, 10),
                    birthdate: player.birthdate,
                }),
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const data = await response.text();
            alert(data); // or handle success
        } catch (error) {
            console.error('Error:', error);
            alert('Failed to save player');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                name="name"
                value={player.name}
                onChange={handleChange}
                placeholder="Name"
                required
            />
            <input
                type="number"
                name="height"
                value={player.height}
                onChange={handleChange}
                placeholder="Height (in cm)"
                // required
            />
            <input
                type="number"
                name="weight"
                value={player.weight}
                onChange={handleChange}
                placeholder="Weight (in kg)"
                // required
            />
            <input
                type="number"
                name="age"
                value={player.age}
                onChange={handleChange}
                placeholder="Age"
                // required
            />
            <input
                type="date"
                name="birthdate"
                value={player.birthdate}
                onChange={handleChange}
                // required
            />
            <button type="submit">Add Player</button>
        </form>
    );
};

export default AddPlayer;
