import React from 'react';
import { useParams } from 'react-router-dom';
import { useState, useEffect } from 'react';

const PlayerPage = () => {
    const { playerId, playerName } = useParams();
    const [games, setGames] = useState([]);
    const [averages, setAverages] = useState({});
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchPlayerDetail();
        fetchPlayerAverages();
    }, [playerId, playerName]);

    const fetchPlayerAverages = async () => {
        try {
            const response = await fetch(`http://localhost:8080/player/${playerId}/averages`);
            if (!response.ok) throw new Error('Network not ok');
            const data = await response.json();
            setAverages(data);
        } catch (error) {
            setError(error.message);
        }
    };

    const fetchPlayerDetail = async () => {
        try {
            const response = await fetch(`http://localhost:8080/player/${playerId}/${playerName}`);
            if (!response.ok) throw new Error('Network response was not ok');
            const data = await response.json();
            setGames(data);
            setLoading(false);
        } catch (error) {
            setLoading(false);
            setError(error.message);
        }
    };

    const uniquePlayerInfo = games.reduce((acc, game) => {
        const playerInfo = `${game.playerId.name} | ${game.playerId.height} | ${game.playerId.weight} | ${game.playerId.age} | ${game.playerId.birthdate}`;
        if (!acc.includes(playerInfo)) {
            acc.push(playerInfo);
        }
        return acc;
    }, []);

    if (loading) return <div className="text-center mt-5"><h3>Loading...</h3></div>;
    if (error) return <div className="text-danger text-center mt-5"><h3>Error: {error}</h3></div>;

    return (
        <div className="containerXX mt-5">
            <h2 className="text-center mb-4">{playerName}</h2>
            <div className="mb-4">
                <h4>Player Info:</h4>
                <ul className="list-group">
                    {uniquePlayerInfo.map((playerInfo, index) => (
                        <li key={index} className="list-group-item">{playerInfo}</li>
                    ))}
                </ul>
            </div>

            <div className="mb-4">
                <h4>Averages:</h4>
                <p>PPG: {averages.PPG} | APG: {averages.APG} | RPG: {averages.RPG}</p>
            </div>

            <div className="container mt-5"></div>
            
        </div>
    );
};

export default PlayerPage;
