import ReactDOM from 'react-dom/client';
import React from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import { useParams, useLocation } from 'react-router-dom';

const data = [
  { filename: "cuhk-2013.jpg", year: 2013, remarks: "Sunset over CUHK" },
  { filename: "cuhk-2017.jpg", year: 2017, remarks: "Bird's-eye view of CUHK" },
  { filename: "sci-2013.jpg", year: 2013, remarks: "The CUHK Emblem" },
  { filename: "shb-2013.jpg", year: 2013, remarks: "The Engineering Buildings" },
  { filename: "stream-2009.jpg", year: 2009, remarks: "Nature hidden in the campus" },
];

class App extends React.Component {
  render() {
    return (
      <>
        <BrowserRouter>
          <div>
            <ul>
              <li>
                <Link to="/">Home</Link>
              </li>
              <li>
                <Link to="/gallery">Images</Link>
              </li>

              <li>
                <Link to="/slideshow">Slideshow</Link>
              </li>
              <li>
                <Link to="/api-tesing">API testing</Link>
              </li>
              <li>
                <Link to="/game-testing">temp link game </Link>
              </li>

            </ul>
          </div>

          <hr />

          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/gallery" element={<Gallery />} />

            <Route path="/slideshow" element={<Slideshow />} />
            <Route path="/api-tesing" element={<Apitesting />} />
            <Route path="/game-testing" element={<Gametesting />} />
            <Route path="*" element={<NoMatch />} />

          </Routes>
        </BrowserRouter>

      </>
    );
  }
}

class Gametesting extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      games: [],
      loading: true,
      error: null
    }
  }
  componentDidMount() {
    this.fetchGameDetail();
  }

  fetchGameDetail = async () => {
    try {
      const response = await fetch('localhost:8080/games/stats');

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      this.setState({games: data, loading: false})


    }
    catch (error){
      console.log("fetch error" + error)
      this.setState({loading: false, error: error.message})
    }
  }
