import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';

const GameStatsRecorder = () => {
    const location = useLocation();
    const { selectedPlayers, selectedGame, games } = location.state || { selectedPlayers: [], selectedGame: '', games: null };

    const selectedGameData = games;

    const [stats, setStats] = useState(selectedPlayers.map(player => ({
        playerId: { id: player.id },
        gameId: { id: selectedGame },
        belongedTeam: player.team,
        points: 0,
        fieldGoalMade: 0,
        fieldGoalAttempted: 0,
        threePointerMade: 0,
        threePointerAttempted: 0,
        freeThrowMade: 0,
        freeThrowAttempted: 0,
        rebounds: 0,
        assists: 0,
        steal: 0,
        block: 0,
        turnover: 0,
    })));

    const handleIncrement = (index, field) => {
        setStats(prevStats => {
            const newStats = [...prevStats];
            newStats[index][field] += 1; // Increment by 1
            return newStats;
        });
    };

    const handleDecrement = (index, field) => {
        setStats(prevStats => {
            const newStats = [...prevStats];
            newStats[index][field] = Math.max(0, newStats[index][field] - 1); // Decrement by 1 but not below 0
            return newStats;
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log('Submitting stats:', stats); // For debugging

        try {
            const response = await fetch('http://localhost:8080/games/stats/save', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(stats),
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const data = await response.json();
            alert('Stats submitted successfully!');
            console.log(data);
        } catch (error) {
            console.error('Error submitting stats:', error);
            alert('E.submitted!');
        }
    };

    const cols = Math.max(2, Math.ceil(12 / selectedPlayers.length));


    return (
        <div className="container mt-5">
            <h4>Selected Game ID: {selectedGameData?.gameNumber} ({selectedGameData?.date})</h4>
            <div className="row">
                {selectedPlayers.map((player, index) => (
                    <div key={player.id} className={`col-${cols} mb-3`}>
                        <div className="card">
                            <div className="card-body">
                                <h5 className="card-title">Player ID: {player.id} ({player.team})</h5>



                                {/* Points */}
                                <div className="mb-0">
                                    <label className="form-label">Points: {stats[index].points}</label>
                                </div>

                                {/* Made FG */}
                                <div className="mb-0">
                                    <label className="form-label">Made FG</label>
                                    <div className="d-flex align-items-center">
                                        <button onClick={() => handleDecrement(index, 'fieldGoalMade')} className="btn btn-secondary me-2">-</button>
                                        <span>{stats[index].fieldGoalMade}</span>
                                        <button onClick={() => handleIncrement(index, 'fieldGoalMade')} className="btn btn-secondary ms-2">+</button>
                                    </div>
                                </div>

                                {/* Missed FG */}
                                <div className="mb-0">
                                    <label className="form-label">Missed FG</label>
                                    <span>{stats[index].fieldGoalAttempted - stats[index].fieldGoalMade}</span>
                                </div>

                                {/* FGA */}
                                <div className="mb-0">
                                    <label className="form-label">FGA</label>
                                    <div className="d-flex align-items-center">
                                        <span>{stats[index].fieldGoalAttempted}</span>
                                    </div>
                                </div>

                                {/* Made 3PT */}
                                <div className="mb-0">
                                    <label className="form-label">Made 3PT</label>
                                    <div className="d-flex align-items-center">
                                        <button onClick={() => handleDecrement(index, 'threePointerMade')} className="btn btn-secondary me-2">-</button>
                                        <span>{stats[index].threePointerMade}</span>
                                        <button onClick={() => handleIncrement(index, 'threePointerMade')} className="btn btn-secondary ms-2">+</button>
                                    </div>
                                </div>

                                {/* Missed 3PT */}
                                <div className="mb-2">
                                    <label className="form-label">Missed 3PT</label>
                                    <span>{stats[index].threePointerAttempted - stats[index].threePointerMade}</span>
                                </div>

                                {/* 3PTA */}
                                <div className="mb-2">
                                    <label className="form-label">3PTA</label>
                                    <div className="d-flex align-items-center">
                                        <span>{stats[index].threePointerAttempted}</span>
                                    </div>
                                </div>

                                {/* REB */}
                                <div className="mb-2">
                                    <label className="form-label">REB</label>
                                    <div className="d-flex align-items-center">
                                        <button onClick={() => handleDecrement(index, 'rebounds')} className="btn btn-secondary me-2">-</button>
                                        <span>{stats[index].rebounds}</span>
                                        <button onClick={() => handleIncrement(index, 'rebounds')} className="btn btn-secondary ms-2">+</button>
                                    </div>
                                </div>

                                {/* AST */}
                                <div className="mb-2">
                                    <label className="form-label">AST</label>
                                    <div className="d-flex align-items-center">
                                        <button onClick={() => handleDecrement(index, 'assists')} className="btn btn-secondary me-2">-</button>
                                        <span>{stats[index].assists}</span>
                                        <button onClick={() => handleIncrement(index, 'assists')} className="btn btn-secondary ms-2">+</button>
                                    </div>
                                </div>

                                {/* STL */}
                                <div className="mb-2">
                                    <label className="form-label">STL</label>
                                    <div className="d-flex align-items-center">
                                        <button onClick={() => handleDecrement(index, 'steal')} className="btn btn-secondary me-2">-</button>
                                        <span>{stats[index].steal}</span>
                                        <button onClick={() => handleIncrement(index, 'steal')} className="btn btn-secondary ms-2">+</button>
                                    </div>
                                </div>

                                {/* BLK */}
                                <div className="mb-2">
                                    <label className="form-label">BLK</label>
                                    <div className="d-flex align-items-center">
                                        <button onClick={() => handleDecrement(index, 'block')} className="btn btn-secondary me-2">-</button>
                                        <span>{stats[index].block}</span>
                                        <button onClick={() => handleIncrement(index, 'block')} className="btn btn-secondary ms-2">+</button>
                                    </div>
                                </div>

                                {/* TOV */}
                                <div className="mb-2">
                                    <label className="form-label">TOV</label>
                                    <div className="d-flex align-items-center">
                                        <button onClick={() => handleDecrement(index, 'turnover')} className="btn btn-secondary me-2">-</button>
                                        <span>{stats[index].turnover}</span>
                                        <button onClick={() => handleIncrement(index, 'turnover')} className="btn btn-secondary ms-2">+</button>
                                    </div>
                                </div>

                                {/* Made FT */}
                                <div className="mb-2">
                                    <label className="form-label">Made FT</label>
                                    <div className="d-flex align-items-center">
                                        <button onClick={() => handleDecrement(index, 'freeThrowMade')} className="btn btn-secondary me-2">-</button>
                                        <span>{stats[index].freeThrowMade}</span>
                                        <button onClick={() => handleIncrement(index, 'freeThrowMade')} className="btn btn-secondary ms-2">+</button>
                                    </div>
                                </div>

                                {/* Missed FT */}
                                <div className="mb-2">
                                    <label className="form-label">Missed FT</label>
                                    <span>{stats[index].freeThrowAttempted - stats[index].freeThrowMade}</span>
                                </div>

                                {/* FTA */}
                                <div className="mb-2">
                                    <label className="form-label">FTA</label>
                                    <div className="d-flex align-items-center">
                                        <span>{stats[index].freeThrowAttempted}</span>
                                    </div>
                                </div>

                            </div>
                        </div>
                    </div>
                ))}
            </div>
            <button type="submit" className="btn btn-primary" onClick={handleSubmit}>Submit Stats</button>
        </div>
    );
};

export default GameStatsRecorder;
