import React from 'react';
import { useParams, useLocation } from 'react-router-dom';
import { useState, useEffect } from 'react';
const PlayerPage = () => {
    const { playerId, playerName } = useParams();
    const [games, setGames] = useState([]);
    const [players, setPlayers] = useState([]);
    const [averages, setAverages] = useState({});
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchPlayerDetail();
        fetchPlayerAverages();
    }, [playerId, playerName]);

    const fetchPlayerAverages = async () => {
        try {
            const response = await fetch(`http://localhost:8080/player/${playerId}/averages`);
            if (!response.ok)
                throw new Error('Network not ok')
            const data = await response.json();
            setAverages(data);

        } catch (error) {
            console.log("fetch error: " + error);
            setError(error.message);
        }

    }

    const fetchPlayerDetail = async () => {
        try {
            const response = await fetch(`http://localhost:8080/player/${playerId}/${playerName}`);
            if (!response.ok) throw new Error('Network response was not ok');
            const data = await response.json();
            setGames(data);
            setLoading(false);
        } catch (error) {
            console.log("fetch error: " + error);
            setLoading(false);
            setError(error.message);
        }
    };
    const uniquePlayerInfo = games.reduce((acc, game) => {
        const playerInfo = `${game.playerId.name}
              ${game.playerId.height}
              ${game.playerId.weight}
              ${game.playerId.age}
              ${game.playerId.birthdate}`;
        if (!acc.includes(playerInfo)) {
            acc.push(playerInfo);
        }
        return acc;
    }, []);
    console.log(players);
    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }
    return (
        <div>
            <div class="ms-2">Player Info:
                {uniquePlayerInfo.map((playerInfo, index) => (
                    <div key={index}>
                        {playerInfo}

                    </div>
                ))}
            </div>
            <div>
                PPG: {averages.PPG} <br />
                APG: {averages.APG} <br />
                RPG: {averages.RPG}
            </div>

            <div>Averages details</div>
            <div class="table-responsive">
                <table class="table table-hover table-bordered">
                    <thead class="table-light">
                        <tr>
                            <th scope="col">Games Played</th>
                            <th scope="col">PTS</th>
                            <th scope="col">FGM</th>
                            <th scope="col">FGA</th>
                            <th scope="col">FG%</th>
                            <th scope="col">3PM</th>
                            <th scope="col">3PA</th>
                            <th scope="col">3P%</th>
                            <th scope="col">FTM</th>
                            <th scope="col">FTA</th>
                            <th scope="col">FT%</th>
                            <th scope="col">REB</th>
                            <th scope="col">AST</th>
                            <th scope="col">STL</th>
                            <th scope="col">BLK</th>
                            <th scope="col">TO</th>
                        </tr>
                    </thead>

                    <tbody>
                        <tr >
                            <th scope="row">{averages.GP}</th>
                            <td>{averages.PPG}</td>
                            <td>{averages.FGM}</td>
                            <td>{averages.FGA}</td>
                            {/* TODO********** */}
                            <td>
                                {averages.TotalFGA > 0 ? ((averages.TotalFGM / averages.TotalFGA) * 100).toFixed(1) + '%' : '0%'}
                                {/* {((game.fieldGoalMade / game.fieldGoalAttempted) * 100).toFixed(2)}% */}
                            </td>
                            <td>{averages.TPM}</td>
                            <td>{averages.TPA}</td>
                            <td>{averages.TotalTPA > 0 ? ((averages.TotalTPM / averages.TotalTPA) * 100).toFixed(1) + '%' : '0%'}</td>
                            <td>{averages.FTM}</td>
                            <td>{averages.FTA}</td>
                            <td>{averages.TotalFTA > 0 ? ((averages.TotalFTM / averages.TotalFTA) * 100).toFixed(1) + '%' : '0%'}</td>
                            <td>{averages.RPG}</td>
                            <td>{averages.APG}</td>
                            <td>{averages.STL}</td>
                            <td>{averages.BLK}</td>
                            <td>{averages.TOV}</td>
                        </tr>
                    </tbody>
                </table>
            </div>

            <div>Game History</div>
            <div class="table-responsive">
                <table class="table table-hover table-bordered">
                    <thead class="table-light">
                        <tr>
                            <th scope="col">Game Date</th>
                            <th scope="col">PTS</th>
                            <th scope="col">FGM</th>
                            <th scope="col">FGA</th>
                            <th scope="col">FG%</th>
                            <th scope="col">3PM</th>
                            <th scope="col">3PA</th>
                            <th scope="col">3P%</th>
                            <th scope="col">FTM</th>
                            <th scope="col">FTA</th>
                            <th scope="col">FT%</th>
                            <th scope="col">REB</th>
                            <th scope="col">AST</th>
                            <th scope="col">STL</th>
                            <th scope="col">BLK</th>
                            <th scope="col">TO</th>
                        </tr>
                    </thead>

                    <tbody>
                        {games.map(game => (
                            <tr key={game.id}>
                                <th scope="row"><a href={`/game/${game.gameId.id}/${game.gameId.date}/${game.gameId.gameNumber}`} style={{ textDecoration: 'none' }}>{game.gameId.date}</a>{ }</th>
                                <td>{game.points}</td>
                                <td>{game.fieldGoalMade}</td>
                                <td>{game.fieldGoalAttempted}</td>
                                <td>{((game.fieldGoalMade / game.fieldGoalAttempted) * 100).toFixed(1)}%</td>
                                <td>{game.threePointerMade}</td>
                                <td>{game.threePointerAttempted}</td>
                                <td>{((game.threePointerMade / game.threePointerAttempted) * 100).toFixed(1)}%</td>
                                <td>{game.freeThrowMade}</td>
                                <td>{game.freeThrowAttempted}</td>
                                <td>{((game.freeThrowMade / game.freeThrowAttempted) * 100).toFixed(1)}%</td>
                                <td>{game.rebounds}</td>
                                <td>{game.assists}</td>
                                <td>{game.steal}</td>
                                <td>{game.block}</td>
                                <td>{game.turnover}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    )
}
    
export default PlayerPage;
