import ReactDOM from 'react-dom/client';
import React from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import { useParams, useLocation } from 'react-router-dom';

const data = [
  { filename: "cuhk-2013.jpg", year: 2013, remarks: "Sunset over CUHK" },
  { filename: "cuhk-2017.jpg", year: 2017, remarks: "Bird's-eye view of CUHK" },
  { filename: "sci-2013.jpg", year: 2013, remarks: "The CUHK Emblem" },
  { filename: "shb-2013.jpg", year: 2013, remarks: "The Engineering Buildings" },
  { filename: "stream-2009.jpg", year: 2009, remarks: "Nature hidden in the campus" },
];

class App extends React.Component {
  render() {
    return (
      <>
        <BrowserRouter>
          <div>
            <ul>
              <li>
                <Link to="/">Home</Link>
              </li>
              <li>
                <Link to="/gallery">Images</Link>
              </li>
              <li>
                <Link to="/slideshow">Slideshow</Link>
              </li>
              <li>
                <Link to="/api-tesing">API testing</Link>
              </li>
              <li>
                <Link to="/game-testing">temp link game </Link>
              </li>
              <li>
                <Link to="/player">player display </Link>
              </li>
            </ul>
          </div>

          <hr />

          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/gallery" element={<Gallery />} />
            <Route path="/slideshow" element={<Slideshow />} />
            <Route path="/api-tesing" element={<Apitesting />} />
            <Route path="/game-testing" element={<Gametesting />} />
            {/* <Route path="/player" element={<PlayerPage />} /> */}
            <Route path="/player/:playerId/:playerName" element={<PlayerPage/>} />
            <Route path="*" element={<NoMatch />} />

          </Routes>
        </BrowserRouter>

      </>
    );
  }
}


class PlayerPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      players: [],
      loading: true,
      error: null
    }
  }
  componentDidMount() {
    this.fetchPlayerDetail();
  }

  fetchPlayerDetail = async () => {
    try {
      const response = await fetch('http://localhost:8080/players');
      if (!response.ok)
        throw new Error('Network response was not ok');
      const data = await response.json();
      this.setState({ players: data, loading: false })
    } catch (error) {
      console.log("fetch error" + error)
      this.setState({ loading: false, error: error.message })
    }
  }


  render() {
    const { players, loading, error } = this.state;
    if (loading) {
      return <div>Loading...</div>;
    }

    if (error) {
      return <div>Error: {error}</div>;
    }
    return (
      <div>
        {players.map(player => (
          <div key={player.id}>
            {player.name}
          </div>
        ))}
      </div>
    )
  }
}

class Gametesting extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      games: [],
      loading: true,
      error: null
    }
  }
  componentDidMount() {
    this.fetchGameDetail();
  }

  fetchGameDetail = async () => {
    try {
      const response = await fetch('http://localhost:8080/games/stats');

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      this.setState({ games: data, loading: false })


    }
    catch (error) {
      console.log("fetch error" + error)
      this.setState({ loading: false, error: error.message })
    }
  }


  render() {
    const { games, loading, error } = this.state;
    const uniqueScores = games.reduce((acc, game) => {
      const score = `${game.gameId.scoreA} - ${game.gameId.scoreB}`;
      if (!acc.includes(score)) {
        acc.push(score);
      }
      return acc;
    }, []);

    if (loading) {
      return <div>Loading...</div>;
    }

    if (error) {
      return <div>Error: {error}</div>;
    }
    return (
      <div>
        <h2 className="ms-2">Game detail testing </h2>
        <div class="ms-2">{uniqueScores.map((score, index) => (
          <div key={index}>{score}</div>
        ))}</div>

        <div class="table-responsive">
          <table class="table table-hover table-bordered">
            <thead class="table-light">
              <tr>
                <th scope="col">Players</th>
                <th scope="col">PTS</th>
                <th scope="col">FGM</th>
                <th scope="col">FGA</th>
                <th scope="col">FG%</th>
                <th scope="col">3PM</th>
                <th scope="col">3PA</th>
                <th scope="col">3P%</th>
                <th scope="col">FTM</th>
                <th scope="col">FTA</th>
                <th scope="col">FT%</th>
                <th scope="col">REB</th>
                <th scope="col">AST</th>
                <th scope="col">STL</th>
                <th scope="col">BLK</th>
                <th scope="col">TO</th>
              </tr>
            </thead>

            <tbody>
              {games.map(game => (
                <tr key={game.id}>
                  <th scope="row"><a href={`/player/${game.playerId.id}/${game.playerId.name}`} style={{ textDecoration: 'none' }}>{game.playerId.name}</a></th>
                  <td>{game.points}</td>
                  <td>{game.fieldGoalMade}</td>
                  <td>{game.fieldGoalAttempted}</td>
                  <td>FG%</td>
                  <td>{game.threePointerMade}</td>
                  <td>{game.threePointerAttempted}</td>
                  <td>3P%</td>
                  <td>{game.freeThrowMade}</td>
                  <td>{game.freeThrowAttempted}</td>
                  <td>FT%</td>
                  <td>{game.rebounds}</td>
                  <td>{game.assists}</td>
                  <td>{game.steal}</td>
                  <td>{game.block}</td>
                  <td>{game.turnover}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}

class Apitesting extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      games: [],
      loading: true,
      error: null,
    };
  }

  componentDidMount() {
    this.fetchGames();
  }

  fetchGames = async () => {
    try {
      const response = await fetch('http://localhost:8080/games');

      if (!response.ok) {
        throw new Error('Network response was not ok');
      }

      const data = await response.json();
      this.setState({ games: data, loading: false });
    } catch (error) {
      this.setState({ error: error.message, loading: false });
    }
  }

  render() {
    const { games, loading, error } = this.state;

    if (loading) {
      return <div>Loading...</div>;
    }

    if (error) {
      return <div>Error: {error}</div>;
    }

    return (
      <div>
        <h2 className="ms-2">API</h2>

        <table class="table table-hover table-bordered">
          <thead class="table-light">
            <tr>
              <th scope="col">Date</th>
              <th scope="col">Game</th>
              <th scope="col">Home Team</th>
              <th scope="col">Away Team</th>
              <th scope="col">Score</th>
            </tr>
          </thead>
          <tbody>
            {games.map(game => (
              <tr>
                <th scope="row"><a href={`/game/${game.id}/${game.date}`} style={{ textDecoration: 'none' }}>{game.date}</a></th>
                <td>{game.gameNumber}</td>
                <td>{game.teamA}</td>
                <td>{game.teamB}</td>
                <td>{game.scoreA} - {game.scoreB}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <ul>
        </ul>
      </div>
    );
  }
}

class Home extends React.Component {
  render() {
    return (
      <div>
        <h2 class="ms-2">Home</h2>
        <img class="ms-2" src="home-diagram.png"></img>
      </div>
    );
  }
}

class Gallery extends React.Component {
  render() {
    return (
      <main className="container">
        {data.map((file, index) => <FileCard i={index} key={index} />)}
      </main>
    );
  }
}

class FileCard extends React.Component {

  constructor(props) {
    super(props);
    this.state = { width: 200 };
  }

  handleMouseDown = () => {
    this.setState({ width: 400 });
  }

  handleMouseUp = () => {
    this.setState({ width: 200 });
  }

  render() {
    let i = this.props.i;
    return (
      <div className="card d-inline-block m-2" style={{ width: this.state.width }} onMouseDown={this.handleMouseDown} onMouseUp={this.handleMouseUp} >
        <img src={"images/" + data[i].filename} className="w-100" />
        <div className="card-body">
          <h6 className="card-title">{data[i].filename}</h6>
          <p className="card-text">{data[i].year}</p>
        </div>
      </div>
    );
  }
}

class Slideshow extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      currentImageID: 0,
      currentInterval: 1500,
      isSlideshowRunning: false,
      isImageResized: false,
      slideshowTimer: null,
    };
  }

  startSlideshow = () => {
    if (!this.state.isSlideshowRunning) {
      this.setState({ isSlideshowRunning: true }, this.runSlideshow);
    }
  };

  runSlideshow = () => {
    const timer = setInterval(() => {
      this.setState((prevState) => ({
        currentImageID: (prevState.currentImageID + 1) % data.length,
      }));
    }, this.state.currentInterval);

    this.setState({ slideshowTimer: timer });
  };

  stopSlideshow = () => {
    this.setState({ isSlideshowRunning: false });
    clearInterval(this.state.slideshowTimer);
  };

  slower = () => {
    this.setState((prevState) => ({
      currentInterval: prevState.currentInterval + 200,
    }));
    if (this.state.isSlideshowRunning) {
      clearInterval(this.state.slideshowTimer);
      this.runSlideshow();
    }
  };

  faster = () => {
    this.setState((prevState) => {
      const newInterval = Math.max(prevState.currentInterval - 200, 200);
      return { currentInterval: newInterval };
    });
    if (this.state.isSlideshowRunning) {
      clearInterval(this.state.slideshowTimer);
      this.runSlideshow();
    }
  };

  resizeImage = () => {
    this.setState((prevState) => ({
      isImageResized: !prevState.isImageResized,
    }));
  };

  componentWillUnmount() {
    clearInterval(this.state.slideshowTimer);
  }

  render() {
    const { currentImageID, isImageResized } = this.state;
    const imageStyle = {
      width: isImageResized ? '20%' : '40%',
    };

    return (
      <div class="ms-3">
        <h2>Slideshow</h2>
        <img
          src={"images/" + data[currentImageID].filename}
          alt={data[currentImageID].remarks}
          style={imageStyle}
        />
        <div>{data[currentImageID].filename}</div>
        {/* <p>Current Interval: {currentInterval} ms</p> */}
        <div>
          <button onClick={this.startSlideshow}>Start Slideshow</button>
          <button onClick={this.stopSlideshow}>Stop Slideshow</button>
          <button onClick={this.slower}>Slower</button>
          <button onClick={this.faster}>Faster</button>
          <button onClick={this.resizeImage}>Resize</button>
        </div>
      </div>
    );
  }
}

// 
function File() {
  let { id } = useParams();
  return (
    <div>
      <h3> ID: {id} </h3>
    </div>
  );
}

function NoMatch() {
  let location = useLocation();
  return (
    <div>
      <h3>
        No Match for <code>{location.pathname}</code>
      </h3>
    </div>
  );
}
// 

const root = ReactDOM.createRoot(document.querySelector('#app'));
root.render(<App />);
