import ReactDOM from 'react-dom/client';
import React from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import { useParams, useLocation } from 'react-router-dom';
import { useState, useEffect } from 'react';

const data = [
  { filename: "cuhk-2013.jpg", year: 2013, remarks: "Sunset over CUHK" },
  { filename: "cuhk-2017.jpg", year: 2017, remarks: "Bird's-eye view of CUHK" },
  { filename: "sci-2013.jpg", year: 2013, remarks: "The CUHK Emblem" },
  { filename: "shb-2013.jpg", year: 2013, remarks: "The Engineering Buildings" },
  { filename: "stream-2009.jpg", year: 2009, remarks: "Nature hidden in the campus" },
];

class App extends React.Component {
  render() {
    return (
      <>
        <BrowserRouter>
          <div>
            <ul>
              <li>
                <Link to="/">Home</Link>
              </li>
              <li>
                <Link to="/gallery">Images</Link>
              </li>
              <li>
                <Link to="/slideshow">Slideshow</Link>
              </li>
              <li>
                <Link to="/api-tesing">API testing</Link>
              </li>
              {/* <li>
                <Link to="/game-testing">temp link game </Link>
              </li> */}
              <li>
                <Link to="/player">All players </Link>
              </li>
            </ul>
          </div>

          <hr />

          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/gallery" element={<Gallery />} />
            <Route path="/slideshow" element={<Slideshow />} />
            <Route path="/api-tesing" element={<Apitesting />} />
            <Route path="/game-testing" element={<Gametesting />} />
            {/* <Route path="/player" element={<PlayerPage />} /> */}
            <Route path="/player/:playerId/:playerName" element={<PlayerPage />} />
            <Route path="/game/:gameId/:gameDate/:gameNumber" element={<GamePage/> } />
            <Route path="*" element={<NoMatch />} />

          </Routes>
        </BrowserRouter>

      </>
    );
  }
}

const GamePage = () => {
  const { gameId, gameDate, gameNumber } = useParams();
  const [games, setGames] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchGameDetail();
  }, [gameId, gameDate, gameNumber]);

  const fetchGameDetail = async () => {
    try {
      const response = await fetch(`http://localhost:8080/game/${gameId}/${gameDate}/${gameNumber}`);
      if (!response.ok) throw new Error('Network response was not ok');
      const data = await response.json();
      setGames(data);
      setLoading(false);
    } catch (error) {
      console.log("fetch error: " + error);
      setLoading(false);
      setError(error.message);
    }
  };
    if (loading) {
      return <div>Loading...</div>;
    }

    if (error) {
      return <div>Error: {error}</div>;
    }
    const uniqueScores = games.reduce((acc, game) => {
      const score = `${game.gameId.scoreA} - ${game.gameId.scoreB}`;
      const date = `${game.gameId.date}`
      if (!acc.includes(score)) {
        acc.push(score);
        acc.push(date)
      }
      return acc;
    }, []);
    console.log(games);
    return (
      <div>
        <h2 className="ms-2">Game detail testing </h2>
        <div class="ms-2">{uniqueScores.map((score, index) => (
          <div key={index}>{score}</div>
        ))} </div>

        <div class="table-responsive">
          <table class="table table-hover table-bordered">
            <thead class="table-light">
              <tr>
                <th scope="col">Players</th>
                <th scope="col">PTS</th>
                <th scope="col">FGM</th>
                <th scope="col">FGA</th>
                <th scope="col">FG%</th>
                <th scope="col">3PM</th>
                <th scope="col">3PA</th>
                <th scope="col">3P%</th>
                <th scope="col">FTM</th>
                <th scope="col">FTA</th>
                <th scope="col">FT%</th>
                <th scope="col">REB</th>
                <th scope="col">AST</th>
                <th scope="col">STL</th>
                <th scope="col">BLK</th>
                <th scope="col">TO</th>
              </tr>
            </thead>

            <tbody>
              {games.map(game => (
                <tr key={game.id}>
                  <th scope="row"><a href={`/player/${game.playerId.id}/${game.playerId.name}`} style={{ textDecoration: 'none' }}>{game.playerId.name}</a></th>
                  <td>{game.points}</td>
                  <td>{game.fieldGoalMade}</td>
                  <td>{game.fieldGoalAttempted}</td>
                  <td>FG%</td>
                  <td>{game.threePointerMade}</td>
                  <td>{game.threePointerAttempted}</td>
                  <td>3P%</td>
                  <td>{game.freeThrowMade}</td>
                  <td>{game.freeThrowAttempted}</td>
                  <td>FT%</td>
                  <td>{game.rebounds}</td>
                  <td>{game.assists}</td>
                  <td>{game.steal}</td>
                  <td>{game.block}</td>
                  <td>{game.turnover}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    );
  }

const PlayerPage = () => {
  const { playerId, playerName } = useParams();
  const [games, setGames] = useState([]);
  const [players, setPlayers] = useState([]);
const [averages, setAverages] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchPlayerDetail();
    fetchPlayerAverages();
  }, [playerId, playerName]);

  const fetchPlayerAverages = async () => {
    try { 
      const response = await fetch(`http://localhost:8080/player/${playerId}/averages`);
      if (!response.ok)
        throw new Error('Network not ok')
      const data = await response.json();
      setAverages(data);

    } catch (error) {
      console.log("fetch error: " + error);
      setError(error.message);
    }

  }

  const fetchPlayerDetail = async () => {
    try {
      const response = await fetch(`http://localhost:8080/player/${playerId}/${playerName}`);
      if (!response.ok) throw new Error('Network response was not ok');
      const data = await response.json();
      setGames(data);
      setLoading(false);
    } catch (error) {
      console.log("fetch error: " + error);
      setLoading(false);
      setError(error.message);
    }
  };
  const uniquePlayerInfo = games.reduce((acc, game) => {
      const playerInfo = `${game.playerId.name}
            ${game.playerId.height}
            ${game.playerId.weight}
            ${game.playerId.age}
            ${game.playerId.birthdate}`;
      if (!acc.includes(playerInfo)) {
        acc.push(playerInfo);
      }
      return acc;
    }, []);
    console.log(players);
    if (loading) {
      return <div>Loading...</div>;
    }

    if (error) {
      return <div>Error: {error}</div>;
    }
    return (
      <div>
        <div class="ms-2">Player Info:
          {uniquePlayerInfo.map((playerInfo, index) => (
            <div key={index}>
              {playerInfo}

            </div>
          ))}
        </div>
        <div>
          PPG: {averages.PPG} <br />
          APG: {averages.APG} <br />
          RPG: {averages.RPG}
        </div>

        <div>Averages details</div>
        <div class="table-responsive">
          <table class="table table-hover table-bordered">
            <thead class="table-light">
              <tr>
                <th scope="col">Games Played</th>
                <th scope="col">PTS</th>
                <th scope="col">FGM</th>
                <th scope="col">FGA</th>
                <th scope="col">FG%</th>
                <th scope="col">3PM</th>
                <th scope="col">3PA</th>
                <th scope="col">3P%</th>
                <th scope="col">FTM</th>
                <th scope="col">FTA</th>
                <th scope="col">FT%</th>
                <th scope="col">REB</th>
                <th scope="col">AST</th>
                <th scope="col">STL</th>
                <th scope="col">BLK</th>
                <th scope="col">TO</th>
              </tr>
            </thead>

            <tbody>
                <tr >
                <th scope="row">{averages.GP }</th>
                <td>{averages.PPG}</td>
                <td>{averages.FGM}</td>
                <td>{averages.FGA}</td>
                {/* TODO********** */}
                  {/* <td>{((game.fieldGoalMade / game.fieldGoalAttempted) * 100).toFixed(2)}%</td>  */}
                <td>{averages.TPM}</td>
                <td>{averages.threePointerAttempted}</td>
                  <td>{((game.threePointerMade / game.threePointerAttempted) * 100).toFixed(2)}%</td>
                  <td>{game.freeThrowMade}</td>
                  <td>{game.freeThrowAttempted}</td>
                  <td>{((game.freeThrowMade / game.freeThrowAttempted) * 100).toFixed(2)}%</td>
                  <td>{game.rebounds}</td>
                  <td>{game.assists}</td>
                  <td>{game.steal}</td>
                  <td>{game.block}</td>
                  <td>{game.turnover}</td>
                </tr>
