import ReactDOM from 'react-dom/client';
import React from 'react';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import { useParams, useLocation } from 'react-router-dom';

const data = [
  { filename: "cuhk-2013.jpg", year: 2013, remarks: "Sunset over CUHK" },
  { filename: "cuhk-2017.jpg", year: 2017, remarks: "Bird's-eye view of CUHK" },
  { filename: "sci-2013.jpg", year: 2013, remarks: "The CUHK Emblem" },
  { filename: "shb-2013.jpg", year: 2013, remarks: "The Engineering Buildings" },
  { filename: "stream-2009.jpg", year: 2009, remarks: "Nature hidden in the campus" },
];

class App extends React.Component {
  render() {
    return (
      <>
        <BrowserRouter>
          <div>
            <ul>
              <li>
                <Link to="/">Home</Link>
              </li>
              <li>
                <Link to="/gallery">Images</Link>
              </li>

              <li>
                <Link to="/slideshow">Slideshow</Link>
              </li>

            </ul>
          </div>

          <hr />

          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/gallery" element={<Gallery />} />

            <Route path="/slideshow" element={<Slideshow />} />
            <Route path="*" element={<NoMatch />} />

          </Routes>
        </BrowserRouter>

      </>
    );
  }
}

class Home extends React.Component {
  render() {
    return (
      <div>
        <h2>Home</h2>
        <img class="ms-2" src="images/cuhk-2013.jpg"></img>
      </div>
    );
  }
}

class Gallery extends React.Component {
  render() {
    return (
      <main className="container">
        {data.map((file, index) => <FileCard i={index} key={index} />)}
      </main>
    );
  }
}

class FileCard extends React.Component {

  constructor(props) {
    super(props);
    this.state = { width: 200 };
  }

  handleMouseDown = () => {
    this.setState({ width: 400 });
  }

  handleMouseUp = () => {
    this.setState({ width: 200 });
  }

  render() {
    let i = this.props.i;
    return (
      <div className="card d-inline-block m-2" style={{ width: this.state.width }} onMouseDown={this.handleMouseDown} onMouseUp={this.handleMouseUp} >
        <img src={"images/" + data[i].filename} className="w-100" />
        <div className="card-body">
          <h6 className="card-title">{data[i].filename}</h6>
          <p className="card-text">{data[i].year}</p>
        </div>
      </div>
    );
  }
}

class Slideshow extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      currentImageID: 0,
      currentInterval: 1500,
      isSlideshowRunning: false,
      isImageResized: false,
      slideshowTimer: null,
    };
  }

  startSlideshow = () => {
    if (!this.state.isSlideshowRunning) {
      this.setState({ isSlideshowRunning: true }, this.runSlideshow);
    }
  };

  runSlideshow = () => {
    const timer = setInterval(() => {
      this.setState((prevState) => ({
        currentImageID: (prevState.currentImageID + 1) % data.length,
      }));
    }, this.state.currentInterval);

    this.setState({ slideshowTimer: timer });
  };

  stopSlideshow = () => {
    this.setState({ isSlideshowRunning: false });
    clearInterval(this.state.slideshowTimer);
  };

  slower = () => {
    this.setState((prevState) => ({
      currentInterval: prevState.currentInterval + 200,
    }));
    if (this.state.isSlideshowRunning) {
      clearInterval(this.state.slideshowTimer);
      this.runSlideshow();
    }
  };

  faster = () => {
    this.setState((prevState) => {
      const newInterval = Math.max(prevState.currentInterval - 200, 200);
      return { currentInterval: newInterval };
    });
    if (this.state.isSlideshowRunning) {
      clearInterval(this.state.slideshowTimer);
      this.runSlideshow();
    }
  };

  resizeImage = () => {
    this.setState((prevState) => ({
      isImageResized: !prevState.isImageResized,
    }));
  };

  componentWillUnmount() {
    clearInterval(this.state.slideshowTimer);
  }

  render() {
    const { currentImageID, isImageResized } = this.state;
    const imageStyle = {
      width: isImageResized ? '25%' : '50%',
    };

    return (
      <div class="ms-3">
        <h2>Slideshow</h2>
        <img
          src={"images/" + data[currentImageID].filename}
          alt={data[currentImageID].remarks}
          style={imageStyle}
        />
        <div>{data[currentImageID].filename}</div>
        {/* <p>Current Interval: {currentInterval} ms</p> */}
        <div>
          <button onClick={this.startSlideshow}>Start Slideshow</button>
          <button onClick={this.stopSlideshow}>Stop Slideshow</button>
          <button onClick={this.slower}>Slower</button>
          <button onClick={this.faster}>Faster</button>
          <button onClick={this.resizeImage}>Resize</button>
        </div>
      </div>
    );
  }
}

// 
function File() {
  let { id } = useParams();
  return (
    <div>
      <h3> ID: {id} </h3>
    </div>
  );
}

function NoMatch() {
  let location = useLocation();
  return (
    <div>
      <h3>
        No Match for <code>{location.pathname}</code>
      </h3>
    </div>
  );
}
// 

const root = ReactDOM.createRoot(document.querySelector('#app'));
root.render(<App />);
