import { render } from '@testing-library/react';
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

const AllPlayers = () => {
    const [playerId] = useParams();
    const [players, setPlayers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        fetchAllPlayers();
        fetchPlayerAverages();
    }, []);

    const fetchPlayerAverages = async () => {
        try {
            const response = await fetch(`http://localhost:8080/player/${playerId}/averages`);
            if (!response.ok)
                throw new Error('Network not ok')
            const data = await response.json();
            setAverages(data);

        } catch (error) {
            console.log("fetch error: " + error);
            setError(error.message);
        }

    }

    const fetchAllPlayers = async () => {
        try {
            const response = await fetch('http://localhost:8080/players');
            if (!response.ok)
                throw new Error('Network not ok')
            const data = await response.json();
            setPlayers(data);
            setLoading(false);
        } catch (error) {
            console.log("fetch error: " + error);
            setLoading(false);
            setError(error.message);
        }
    }
    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
      }

    return (
        <>
            <div>
            {players.map(player => (
                <div key={player.id} class="card" style={{ width: '18rem' }}>
                    <div class="card-body">
                        <h5 class="card-title">{ player.name}</h5>
                        <p class="card-text"> </p>
                        <a href={`/player/${player.id}/${player.name}`} style={{ textDecoration: 'none' }}>Game Averages</a>
                    </div>
                </div>
            ))}
            </div>
            
        </>
    )
}

export default AllPlayers;
